package java_Git_jimin;
import java.util.*;
public class Ex_for5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		int haq = 0;
		
		for (i = 1; i <= 10; i++) {
			haq += i;
		}
		System.out.printf("%d \n", haq);
		
		int hap = 0;
		for(int j = 501; j <= 1000; j += 2) {
			hap += j;
		}
		System.out.printf("%d", hap);
	}
	
}
