package java_Git_jimin;
import java.util.*;
public class Ex_for6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//구구단을 만들어보자
		Scanner sc = new Scanner (System.in);
		
		int i;
		int j;
		int x;
		System.out.println("구구단을 알려드릴꼐요 몇단? : ");
		x = sc.nextInt();
		for(i = 1; i <= i; i++) {
			j = x * i;
			System.out.printf("%d * %d = %d \n", x,i,j);
		}
		
	}

}
